function getRandomCategories(numCategories: number) {
  // Copia della lista per evitare modifiche alla lista originale
  const shuffled = [...categorieTeatrali].sort(() => 0.5 - Math.random());

  // Restituisce un numero casuale di categorie richieste
  return shuffled.slice(0, numCategories);
}

export async function addVideo() {
  for (const category of categorieTeatrali ) {
    await axios.post('/api/categories', { value: category})
  }

  for (const video of videoRecitazione) {
    video.categories = getRandomCategories(Math.floor(Math.random()*categorieTeatrali.length)).map(c => ({"name": c}))
    await axios.post('/api/videos', { value: video });
  }
}
